stream = ""


def find_marker(stream, marker_length):
	for n in range(marker_length, len(stream)):
		candidate = stream[n - marker_length: n]
		if len(set(candidate)) == marker_length:
			return n


print(find_marker(stream, marker_length=4))
print(find_marker(stream, marker_length=14))

